define(["connecta.portal","portal/layout/service/notify"],function(portal){return portal.service("InviteService",["$q","DomainService",function($q,DomainService){var InviteService=this;InviteService.invite=function(idDomain,emails){var deferred=$q.defer();if(emails){DomainService.inviteUser(emails,idDomain).then(function(){deferred.resolve()},function(){deferred.reject()})}else{deferred.reject()}return deferred.promise}}])});