define(["connecta.portal","portal/layout/directive/layout"],function(portal){return portal.controller("appMain",["$scope","applicationsService","applications",function($scope,applicationsService,applications){applicationsService.setApplications(applications);resetStyles=function(){$scope.stylesheets=[];for(var a in applications){if(!applications.hasOwnProperty(a)){continue}var application=applications[a];$scope.stylesheets.push("assets/css/"+application.name+".css")}};resetStyles();$scope.$on("applications.change",function(event){resetStyles();var appInstance=applicationsService.getInstance();if(appInstance&&appInstance.styles){$.each(appInstance.styles,function(i,item){$scope.stylesheets.push(appInstance.host+"/resources/css/"+item)})}$scope.actualAppName=appInstance.name})}])});