define(["connecta.portal"],function(portal){return portal.directive("randomClass",function(){var _tempSelectedClasses=[];var _useCount=-1;return{scope:{classes:"=randomClass",list:"=randomClassList",item:"=randomClassItem",sequential:"=randomClassSequential"},link:function(scope,element,attributes){function _randomClass(){if(_tempSelectedClasses.length==scope.classes.length){_tempSelectedClasses=[]}var selected;do{selected=scope.classes[Math.floor(Math.random()*scope.classes.length)]}while(_tempSelectedClasses.indexOf(selected)>=0);_tempSelectedClasses.push(selected);return selected}function _nextSequentialClass(){return scope.classes[++_useCount%scope.classes.length]}if(scope.sequential){element.addClass(_nextSequentialClass())}else{element.addClass(_randomClass())}}}})});